import kotlinx.android.synthetic.main.activity_hero.*

class HeroActivity : AppCompatActivity() {

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        val arguments = intent.extras
        val name = arguments!!.get("name")!!.toString()
        setContentView(R.layout.activity_hero)
        heroAboutTxt.movementMethod = ScrollingMovementMethod()

        val heroName = when(name) {
            "dio" -> "Dio Brando"
            "jonathan" -> "Jonathan Jostar"
            "erina" -> "Erina Pendleton"
            "spidwagon" -> "Robert E O Speedwagon"
            "uill" -> "Uill Antonio Zeppeli"
            "george" -> "George Jostar"
            "dario" -> "Dario Brando"
            "joseph" -> "Joseph Jostar"
            "lisalisa" -> "Lisa Lisa"
            "cesar" -> "Antonio Cesar Zeppeli"
            "shtroihaim" -> "Rudolf Schtroiheim"
            "kvatro" -> "Siusey Kvatro"
            "pillars" -> "Humans in pillar"
            "jotaro" -> "Jotaro Kujo"
            "holly" -> "Holly Kujo"
            "abdul" -> "Mohammed Abdul"
            "kakyoin" -> "Kakyoin Noriaki"

            else -> "error"
        }

        val heroAbout = when(heroName) {
            "Dio Brando" -> "Дио Брандо (яп. ディオ・ブランドー Дио Бурандо:) — Главный злодей 1 и 3 части манги. Он сын алкоголика-вора и поэтому ненавидит своего отца. После его смерти попал в поместье Джостаров и стал приёмным сыном. Через 6 лет он начинает подсыпать яд в лекарство больного отца Джонатана, чтобы после его смерти заполучить наследство. Однако Джонатан раскрывает преступление Дио и нападает на него. Дио же решает испытать на себе каменную маску и превращается в вампира, однако его побеждает Джонатан, а сам Дио оказывается серьёзно раненным. Он какое-то время восстанавливал своё тело, выпивая кровь у людей. Также Дио начал воскрешать мёртвых героев и отправлять их сражаться с Джонатаном. Во время последней битвы с Джонатаном, опять проигрывает, так как Джонатан вводит в его тело хамон, энергию, которая разрушает вампира, и чтобы спастись, Дио отделяет свою голову от тела. Позже Дио решает заполучить тело Джонатана и отправляется тайно со своим слугой на круизный лайнер. Джонатан однако подрывает лайнер, принеся себя в жертву и уходит вместе с головой Дио под воду."
            "Jonathan Jostar" -> "Джонатан Джостар (яп. ジョナサン・ジョースター Дзёнасан Дзё:сута:) — Главный герой первой части манги. Родом из благородной семьи Джостаров. Мать Джонатана умерла практически сразу после его рождения, защищая его своим телом во время крушения повозки. Джонатан рос сильным, но когда в поместье прибыл Дио, он всеми способами пытался сломить волю Джонатана: оскорблениями, побоями и убийством его любимой собаки по кличке \"Актуатор\". При этом отец Джонатана был всегда на стороне Дио, будучи уверенным, что Джонатан лжёт об издевательствах и просто хочет «выжить» Дио из поместья. Прошло 6 лет, Дио и Джонатан к тому времени стали более нормально уживаться. Впрочем вскоре Джонатан узнает о том, что Дио собирается отравить его отца, чтобы по-быстрее заполучить наследство. Этому не суждено сбыться ,планы Дио были разрушены, из-за чего последний случайно находит каменную маску, превратившую его в вампира. В тяжелом бою последний Джостар одолевает его, сжигая дотла поместье. Джонатан начинает путешествовать в поисках Дио, чтобы уничтожить его. К нему присоединяется барон Цеппели, обладающий необычной силой «Хамон», овладение которой поможет одолеть вампира. Так Джонатан начинает обучаться использованию данной силы. Дио направляет своих подчинённых — убить Джонатана, но герой одолевает их всех. В конце концов он фактически уничтожает Дио, введя Хамон в его тело, но тот спасается, отделив свою голову. После помолвки Джонатан и Эрина отправляются на круизный корабль в Америку. Но там оказывается и Брандо, который намерен заполучить тело Джонатана, тогда он обратил всех людей на корабле в зомби. Джонатан подрывает корабль, потопив себя, Дио и остальных"
            "Erina Pendleton" -> "Эрина Пендлетон (яп. エリナ・ペンドルトン Эрина Пэндорутон) — Девушка Джонатана, начала встречаться с ним тайно будучи подростком. Когда Дио узнал о отношениях Джонатана с ней, он украл её первый поцелуй, чтобы Эрине было стыдно появляться на глазах у Джонатана. Через много лет Эрина и Джонатан женятся, и после смерти Джонатана Эрина спасла новорожденную девочку из тонущего корабля и уже несла сына от Джонатана в своём чреве. Известно, что будущий сын Джордж II не обладал никакими способностями и умер после атаки вампира, а подобранная девочка в будущем стала женщиной-воином и родила сына от Джорджа II — Джозефа Джостара, главного героя второй части манги. Во второй части Эрина уже становится пожилой женщиной и живёт в Нью-Йорке, опекая своего внука Джозефа."
            "Robert E O Speedwagon" -> "Роберт Э. О. Спидвагон (яп. ロバート・E・O・スピードワゴン Роба:то Э О Супи:довагон) — Появляется впервые в Лондоне как глава разбойной шайки из Огр-стрит, но, увидев силу Джонатана, быстро меняет своё мнение о нём и решает стать верным попутчиком. Однако не может использовать сверхъестественную силу и во многих ситуациях остаётся бессильным. Перед событиями второй части Роберт обнаруживает нефтяную скважину и основывает нефтяную компанию Спидвагон, став миллиардером. Но и не отказывается от своей цели — уничтожить каменные маски. Во второй части ему уже 75 лет, и Спидвагона берут в заложники нацисты, где он становится свидетелем пробуждения человека из колонны, однако его спасает Джостар. В 1952 году Роберт умирает от сердечного приступа в возрасте 89 лет. Его компания и по сей день помогает семье Джостаров."
            "Uill Antonio Zeppeli" -> "Уилл Антонио Цеппели (яп. ウィル・A・ツェペリ Уиру А Цэппэри) — В молодости плыл с экипажем, изучавшим руины ацтеков. Его отец надел на себя каменную маску и обратился в вампира. Все члены экипажа были убиты, однако Цеппели был спасён благодаря восходящему солнцу. После этого он отправился в Тибет, чтобы изучить технику Хамон и разрушить маску. Его обучал мастер Тонпетти, который предсказал, что если Цеппели изберёт путь воина света, то умрёт страшной смертью, защищая «молодого льва». Позже Уилл встретил Джонатана и начал обучать его технике хамон, и параллельно сражаться против Дио и его приспешников. Впоследствии Таркус, который был оживлён Дио, разорвал тело Цеппели пополам, перед смертью он отдаёт всю силу Хамона Джонатану, который находился при смерти и спас его, после чего умирает на руках Джонатана. Он назван в честь музыкальной группы Led Zeppelin."
            "George Jostar" -> "Джордж Джостар (яп. ジョージ・ジョースター Дзёдзи Дзёсута:) — глава поместья Джостаров в первой части и отец Джонатана. Подвергся нападению Дарио Брандо, в результате чего умерла его жена, но ошибочно принял Дарио за спасителя и позже решил приютить его сына — Дио Брандо. Гораздо более лояльно относился к Дио, в упор не замечая, что тот издевается над Джонатаном. Позже Джордж начал умирать от непонятной болезни, но позже выяснилось, что Дио подсыпал ему яд. После того, как планы Дио провалились, он попытался убить Джонатана, но Джордж подставил своё тело и умер на руках сына."
            "Dario Brando" -> "Дарио Брандо (яп. ダリオ・ブランドー Дарио Бурандо:) — отец Дио Брандо. Алкоголик и вор. Напал на повозку семьи Джостаров, где был новорожденный Джонатан, и в результате притворился спасителем, добившись лояльного отношения со стороны Джорджа. Очень грубо и жестоко обращался с маленьким Дио, в результате воспитал в нём злость и алчность. Сам Дио тоже ненавидел отца, и даже после смерти плюнул в гроб, хотя благодаря нему попал в поместье Джостаров."
            "Joseph Jostar" -> "Джозеф Джостар (яп. ジョセフ・ジョースター Дзёдзэфу Дзё:сута:) — Главный герой 2 части истории и один из главных — в 3 части и второстепенный персонаж в 4 части. Он внук Джонатана Джостара. Может как и дед использовать Хамон, очень проницательный и расчётливый, практически всегда предугадывает действия противника. По сюжету манги Battle Tendency, вступает в сражение с древними вампирами и одерживает победу. В 3 части Джозефу уже 69 лет и из-за пробуждения Дио приобретает стенд, который способен передавать изображения и мысли на телевизор или фотоаппарат, а также приобретать форму длинных кустов. Вытаскивает Дзётаро из тюрьмы и помогает ему понять свою силу и победить Дио. Если в OVA он показан серьёзно настроенным стариком, повидавшим жизнь, то в манге и сериале он всё ещё, как в свою молодость преисполнен энтузиазмом, хитростью и чувством юмора, от чего частично выполняет роль комичного персонажа. Его стенд — «пурпурный отшельник» назван в честь карты Отшельник и представляет собой колючие лозы, способные обездвиживать или душить противника, также стенд способен указывать место цели. В 1999 году показано, что Джозеф уже глубоко стар и в Морио он впервые знакомится со своим сыном и случайно находит полностью невидимую новорожденную девочку, владеющую стендом Achtung Baby и не найдя её родителей, решает стать ей новым отцом, назвав Сидзукой Джостар. По версии сайта theotaku2anime.wordpress.com, занимает 1 место в списке лучших персонажей вселенной Jojo, также критик отметил, что Джозеф, в отличие от первого героя, не страдает «синдромом рыцаря» и может пойти на хитрые уловки, но его цели всё равно остаются благородными."
            "Lisa Lisa" -> "Лиза Лиза (Элизабет Джостар) (яп. リサリサ（エリザベス・ジョースター） Ридзаридза (Эридзабэсу Дзё:сута:)) — Мать Джозефа Джостара. Появляется впервые младенцем в конце первой части, в корабле, атакованном Дио, её мать была обращена в зомби, но Лизу спас Джонатан и перед своей смертью и взрывом корабля, передал Эрине. Была в результате удочерена Страйзо, который обучил её технике хамона. Она вышла замуж за Джорджа Джостара II, сына Эрины и Джонатана, который однако не обладал никакими супер-силами, и родила Джозефа. Однако Джордж был вскоре убит зомби, служившим Дио. Элизабет в знак мести убивает убийцу и сжигает его тело дотла, однако он оказывается высокопоставленным военным чиновником и чтобы избежать возмездия, заручилась поддержкой со стороны фонда «Спидвагон», который стёр все её следы существования. Так многие бывшие друзья и родственники думали, что она умерла. Элизабет под псевдонимом «Лиза Лиза» выступала в качестве наставника Цезаря Цеппели и тайно помогает в борьбе против людей из колонн. Она признаётся Джозефу в том, что она его мать, и позже повторно вступает в брак с голливудским сценаристом и переезжает в Америку."
            "Antonio Cesar Zeppeli" -> "Цезарь Антонио Цеппели (яп. シーザー・アントニオ・ツェペリ Си:дза: Антонио Цэппэри) — Внук Уилла Цеппели, владеет хамоном на высшем уровне, как и дед. Дамский угодник. Ещё в детстве отец — Марио Цеппели бесследно покинул семью. Цеппели был после обманут родственниками. Это сделало мальчика жестоким, он возненавидел отца и отказался от своей фамилии. Цезарь рос разбойником, а позже стал главарём мафии. В 16 лет внезапно встретил отца, который отправлялся к раскопкам в Колизее. Там Марио жертвует своей жизнью, чтобы спасти сына от ловушки и его засосало в колонну с каменными статуями. Цезарь понял, что отец исчез, чтобы не вмешивать в эту опасность родственников. Марио сказал Цезарю отправился к Лизе Лизе. Сначала плохо относился к Джозефу из-за того, что из-за его деда умер дед Цезаря, в ответ Джозеф обзывал его макаронником и бабником. Но с течением времени отношения между ними нормализуются. Во время битвы против людей из колонны перед смертью передаёт добытое кольцо Джозефу."
            "Rudolf Schtroiheim" -> "Рудольф фон Штрохайм (яп. ルドル・フォン・シュトロハイム Рудору фон Сюторохайму) — лидер нацистской группировки в Мексике. Сначала он подбирает Спидвагона, ранее пострадавшего от рук Стрэйдза, чтобы узнать, кто такой человек из колонны и как его оживить. Для оживления он пользуется кровью пленников и даёт гуманоиду имя Сантана. Однако Сантана оказался слишком опасен, и потому, жертвуя собой, Штрохайм помогает Джозефу Джостару победить его. Позже Штрохайм появляется вместе с Карсом, уже в виде киборга, чтобы помешать второму заполучить камень Эйдзи и, опять же, помочь Джозефу. Он и дальше помогает главным героям, позже примкнув к фонду Спидвагона. В конце становится известно, что Штрохайм, больше не увидевшись с ДжоДжо со времён борьбы с людьми из колонны, погиб в 1943 году в битве за Сталинград."
            "Siusey Kvatro" -> "Сьюзи Кватро (яп. スージーQ Судзи Куатуро) — помощница Лизы Лизы. Быстро дружится с Джозефом и в конечном счёте влюбляется в него. Позже её телом овладевает мозг Эйсидиси, который отправляет посылку с красным камнем Эйши в Швейцарию. Впоследствии Джозеф уничтожает мозг Эйсидиси, не навредив Сьюзи. В 1939 году выходит замуж за Джозефа и рожает Холли Кудзё/(Джостар), которая в свою очередь станет матерью Дзётаро Кудзё. В 3 части показана пожилой женщиной, которая пристально следит за состоянием Холли."
            "Humans in pillar" -> "4 могущественных гуманоида и представители древней расы, которую человечество почитало как богов и демонов. Они существовали до людей, питаясь энергией растений и животных. Они сильнее, умнее людей, однако боятся солнца и стали править молодым человечеством, питаясь их энергией и кровью. Однако против них восстал «народ солнца», чьи представители владели хамоном, но были истреблены, хотя некоторые из них растворились среди людей, в результате по сей день могут рождаться потомки, владеющие этой энергией. Карс, представитель сверх-расы создал каменные маски в надежде, что они избавят его народ от слабости к свету. Хотя маска и делала их сильнее, она также побуждала сильную жажду. Сородичи испугались, что они истребят всё живое на земле и ополчились против Карса. В ответ Карс и его союзник Эйсидиси уничтожили свой народ (в том числе родителей Карса), за исключением Вамуу и Сантаны. Они также обнаружили, что маска обращает человека в вампира и частично наделяет способностями своей расы, однако не делает их полноценными представителями своего вида. Для того, чтобы усовершенствовать маску, им нужно найти красный камень и стать совершенной формой жизни. Однако они не находят камень и впадают в спячку на 2000 лет до того момента, как во время Второй мировой войны нацисты не отправляют их в лаборатории и оживляют с помощью крови узников. Примечательно, что имена четырёх людей из колонны носят имена известных рок-групп 80-х годов: Сантана — Santana, Эйсидиси — AC/DC, Вам — Wham!, Карс — The Cars.\n" +
                    "Критик сайта kotaku.com похвалил персонажей за их колоритность, которая однако не доходит до уровня Дио Брандо, оставляя за ним право быть главным злодеем.[2]\n" +
                    "\n" +
                    "Сантана (яп. サンタナ)\n" +
                    "Сэйю: Кэндзи Номура\n" +
                    "Человек из колонны. Впервые был обнаружен в мексиканской пирамиде в 1938 году, был пробуждён нацистами с помощью крови еврейских пленников, которая хотела укрепить нацистскую армию. Способен проходить через отверстия до 4 сантиметров, однако для этого ломает свои кости, и может вселяться в тело человека, поглощая его. Единственный, кто теоретически не был убит Джозефом, так как обратился в камень под действием солнечных лучей, но без них сразу может прийти обратно к жизни. Ему примерно около 10000 лет[7].\n" +
                    "Эйсидиси (яп. エシディシ Эсидиси)\n" +
                    "Сэйю: Кэйдзи Фудзивара\n" +
                    "Человек из колонны, ему свыше 92000 лет[7]. Был найден нацистами под Колизеем в Риме. Может увеличивать температуру тела до 500 ° С, его кровь может принимать форму игл, которая разъедает жертву изнутри. Имеет неуравновешенный характер и впадает в истерику при виде собственной раны, чем вводит в ступор противника. Бывал в древнем Китае и знал Сунь-цзы. Занимался поиском красного камня и узнал, что он у Лизы-Лизы. Во время сражения с Джозефом, его тело было уничтожено, но мозг стал паразитировать на теле Джозефа, который переместился в тело Сьюзи Кватро, овладев её телом. После чего отправил камень Эйши в Швейцарию. Он также хотел уничтожить главных героев, но Джозеф окончательно уничтожает мозг Эйсидиси, оставив под солнцем, не навредив Сьюзи.\n" +
                    "Вам (яп. ワムウ Вамуу:)\n" +
                    "Сэйю: Акио Оцука\n" +
                    "Слуга Эйсидиси и Карса. Сильный и благородный воин, обладающий силой ветра, нападает рефлекторно на любого, кто наступает на его тень. Может отращивать на лбу рог, улавливающий звуковые волны. Может с помощью ветра создавать разрушительную волну. По своей силе даже превосходит остальных. Был уничтожен Джозефом.\n" +
                    "Карс (яп. カーズ Ка:дзу)\n" +
                    "Сэйю: Кадзухико Иноуэ\n" +
                    "Главный среди людей из колонны. Ему уже более 102,000 лет[7]. Безжалостен к людям и готов убивать их, при сражении идёт на подлые поступки, обманывая врагов. Может изменять своё тело, выделяя из него острые костяные конечности. Карсу удаётся соединить каменную маску с камнем Эйши, и он становится неуязвимым к солнечному свету, используя хамон 100-кратной силы. В финальной битве отрезает Джозефу левую руку, но тот, прибегнув к смекалке, с помощью извержения вулкана запускает Карса в космос, в конце концов Карс оказывается замороженным и улетает в космическое пространство."
            "Jotaro Kujo" -> "Дзётаро Кудзё (яп. 空条 承太郎 Кудзё: Дзётаро:) — главный герой третьей части. Он хафу — сын японского музыканта Садао Кудзё и Холли Кудзё, дочки Джозефа Джостара. Сначала Дзётаро добровольно сидел в тюрьме, так как верил, что его стенд является злым духом, и даже пытался застрелиться. После возвращения Дио, Холли начала медленно умирать из-за нестабильной силы в её теле. Так Дзётаро Куджо, Мохаммед Абдул и Джозеф Джостер начинают путешествовать по странам востока начиная с Гонконга, Сингапура, Калькутты, Пакистана, Карачи и заканчивая свои путешествия в Каире. Во время путешествия к нему присоединяются Нориаки Какёин, Жан-Пьер Польнарефф и пёс Игги. Во время финальной битвы обнаруживает, что его стенд способен останавливать время. В 4 части ему 29 лет, и он сотрудничает с компанией Спидвагон, Дзётаро также периодически отправляется в Морио, чтобы помочь Дзёсукэ. В четвёртой часто он демонстрирует идеальную интуицию и глубокие познания окружающего мира, что делает из него хорошего сыщика и стратега. В 2011 году (6 часть) Дзётаро уже 42 года, и он становится океанологом, переезжая в США, там он растит дочь Джолин Кудзё, которая тоже обладает сверхспособностями и становится главной героиней 6 части. Позже он пытается забрать Джолин из тюрьмы, и в результате стенд по имени Вайтснейк крадёт у Дзётаро его стенд и память, преобразовав их в диски. Джолин пытается спасти отца, так как без них Дзётаро погибнет. У неё это получается, и он помогает ей и её друзьям в финальной битве с отцом Пуччи, но погибает в основной вселенной, не успевая нанести последний удар по Пуччи. В альтернативной вселенной без Пуччи, он остался жив и у него другое имя. Способность Дзётаро названа в честь карты Звезда."
            "Holly Kujo" -> "Холли Кудзё (яп. 空条 ホリィ Кудзё: Хори) — дочь Джозефа Джостара и мать Дзётаро Кудзё. В свои 20 лет вышла замуж за японского музыканта Садао Кудзё и переехала в Японию. После появления Джозефа, который добился освобождения Дзётаро из тюремного заключения, она узнаёт о Дио Брандо, который владеет теперь телом её прадеда, и внутри Холли пробуждается суперсила, которая медленно разрушает её слабую и нежную душу. В результате она впадает в кому, а из её тела выходит стенд в форме виноградной лозы с розами. После поражения Дио снова возвращается в нормальную форму."
            "Mohammed Abdul" -> "Мохаммед Абдул (яп. モハメド・アヴドゥル Мохамэдо Абудуру) — пророк, родом из Каира, Египта. Он очень серьёзный и располагает глубокими знаниями. До начала основных событий приручил бездомную собаку Игги. Был одним из первых, кто встретил Дио после воскрешения, и тогда чуть не потерял жизнь, но со страхом сбежал. Впервые появляется, сопровождая Джозефа Джостара, чтобы оценить сложившуюся ситуацию, когда Дзётаро попал в тюрьму. Может высвобождать стенд в образе большого красного орла и манипулировать огнём. Выступает в качестве мудреца. Его стенд — Magician's Red, похож на огненного феникса и может атаковать огнём и узнавать местонахождение противника по теплу. Стенд назван в честь карты Маг. Был смертельно ранен, и какое-то время читатель думал, что Aбдул погиб, но на деле он выжил. Был мгновенно убит стендом Ваннилы Айс, переместившись в другое измерение. При этом отрезанные конечности остались."
            "Kakyoin Noriaki" -> "Нориаки Какёин (яп. 花京院 典明 Какё:ин Нориаки) — изначально в качестве студента он путешествовал в Египет. Со стороны он кажется хладнокровным, но на самом деле у него тёплое сердце и он верный боец. После некоторых событий Нориаки приобретает большую уверенность в себе. Суперсилы он раскрыл во время борьбы с Дио, однако оказался под его контролем. Нападение Нориаки на Дзётаро значительно отличается в OVA 2000 года, так в манге, сериале 2014 года (и видео-игре) внутри клиники школы он контролирует тело медсестры, в OVA для нападения он использует тело студентки. Но в любом случае терпит поражение от Дзётаро и освобождается от рабства Дио, после чего присоединяется к Дзётаро. Позже, во время битвы с Дуром Бидзаррэ, глаза Нориаки были повреждены, и он временно отстал от героев, но позже вернулся в солнечных очках. Во время финальной битвы с Дио умирает, но успевает передать секрет стенда Дио. Его стенд — Hierophant Green состоит из множества мембран, которые могут обездвиживать противника, брать его тело под контроль и взрываться, стенд назван в честь карты Проповедник."

            else -> "error"
        }


        heroNameTxt.text = heroName
        heroAboutTxt.text = heroAbout

    }
}
